import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import { useState } from "react";
import styled from "styled-components";
import { mobile } from "../responsive";
import * as React from 'react';

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  position: relative;
  overflow: hidden;
`;

const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: #fff7f7;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => props.direction === "left" && "10px"};
  right: ${(props) => props.direction === "right" && "10px"};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
  z-index: 2;
`;

const Wrapper = styled.div`
  height: 100%;
  display: flex;
  transition: all 1.5s ease;
  transform: translateX(${(props) => props.slideIndex * -100}vw);
`;

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
  background-color: #000000;
  z-index: -1;
  ${mobile({ flexDirection: "column", justifyContent: "center" })}
`;

const ImgContainer = styled.div`
  height: 80%;
  flex: 1;
  padding-left: 100px;
  ${mobile({ paddingLeft: "0", height: "50%" })}
`;

const Image = styled.img`
  height: 80%;
  ${mobile({ height: "100%" })}
`;

const InfoContainer = styled.div`
  flex: 1;
  color: white;
  padding: 50px;
  ${mobile({ padding: "20px", textAlign: "center" })}
`;

const Title = styled.h1`
  font-size: 70px;
  ${mobile({ fontSize: "40px" })}
`;

const Desc = styled.p`
  margin: 50px 0px;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
  ${mobile({ margin: "20px 0px", fontSize: "16px" })}
`;

const Button = styled.button`
  padding: 10px;
  font-size: 20px;
  background-color: transparent;
  border: 1px solid white;
  color: white;
  cursor: pointer;
  ${mobile({ fontSize: "16px", padding: "8px" })}
`;

const Link = styled.a`
  text-decoration: none;
  color: white;
`;

const Slider = () => {
  const [slideIndex, setSlideIndex] = useState(0);
  const handleClick = (direction) => {
    if (direction === "left") {
      setSlideIndex(slideIndex > 0 ? slideIndex - 1 : 2);
    } else {
      setSlideIndex(slideIndex < 2 ? slideIndex + 1 : 0);
    }
  };
  return (
    <Container>
      <Arrow direction="left" onClick={() => handleClick("left")}>
        <ArrowLeftOutlined />
      </Arrow>
      <Wrapper slideIndex={slideIndex}>
        <Slide>
          <ImgContainer>
            <Image src="https://codess.cafe/images/banner.png" />
          </ImgContainer>
          <InfoContainer>
            <Title>CODESS Cafe</Title>
            <Desc>Get the opportunity to join in a community</Desc>
            <Button>
              <Link href="https://codess.cafe/" target="_blank">Details</Link>
            </Button>
          </InfoContainer>
        </Slide>
        <Slide>
          <ImgContainer>
            <Image src={require("../components/learn_for_cause_lfc_logo.jpeg")} />
          </ImgContainer>
          <InfoContainer>
            <Title>Learn For Cause</Title>
            <Desc>Code For Cause</Desc>
            <Button>
              <Link href="https://in.linkedin.com/company/learn-for-cause-lfc" target="_blank">Details</Link>
            </Button>
          </InfoContainer>
        </Slide>
        <Slide>
          <ImgContainer>
            <Image src="https://cdn.hashnode.com/res/hashnode/image/upload/v1643230111752/uv4w72h8g.png" />
          </ImgContainer>
          <InfoContainer>
            <Title>HASHNODE</Title>
            <Desc>Home for tech writers and leaders</Desc>
            <Button>
              <Link href="https://hashnode.com/" target="_blank">Details</Link>
            </Button>
          </InfoContainer>
        </Slide>
      </Wrapper>
      <Arrow direction="right" onClick={() => handleClick("right")}>
        <ArrowRightOutlined />
      </Arrow>
    </Container>
  );
};

export default Slider;
